#include <stdint.h>
#include <stdio.h>
#include "Timer.h"
#include "extin.h"
#include "uart.h"
#if !defined(__SOFT_FP__) && defined(__ARM_FP)
#warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

extern uint32_t count;

int main(void)
{
	char str[10];
	int duty;
	int cc=1;
	UartInit(9600);
	TimerPwmInit();
	extint_init();
	while(1)
	{
		if(count>=1)
		{
			count=0;
			if(cc>100)
				cc=1;

			for(duty=0; duty<100;duty+=cc)
			{
				TIM8->CCR1 = duty;
				sprintf(str,"%d\r\n",duty);
				UartPuts(str);
				DelayMs(40);
			}
		}

		else
		{
			cc+=10;

			for(duty=99;duty>=0;duty-=cc)
			{
				TIM8->CCR1 = duty;
				sprintf(str,"%d\r\n",duty);
				UartPuts(str);
				DelayMs(40);
			}
		}
	}

}

